(()=>{"use strict";(async()=>{await async function(){let e=await(async()=>(await fetch("http://localhost:3000/games")).json())();const t=document.createElement("div");t.setAttribute("id","games-list"),t.setAttribute("class","row"),document.getElementById("games").appendChild(t);for(var a,n=0;n<e.length;n++){let t=document.createElement("div");t.setAttribute("class","col-6 col-sm-3"),document.getElementById("games-list").appendChild(t).innerHTML=`<div class="game-snippet">\n    <a href="/detail.html?id=${(a=e[n]).id}">\n      <div class="game-snippet-container">\n        <img class="img-thumbnail" id="game-${a.id}" src="${a.image}" alt="Cover of game ${a.name}" />\n      </div>\n      <h3 class="game-name">${a.name}</h3>\n    </a>\n  </div>`}}()})()})();